package com.rebell.model.mysql;


import com.rebell.enums.TemplatePlanType;
import lombok.Getter;
import lombok.Setter;

import jakarta.persistence.CascadeType;
import jakarta.persistence.Column;
import jakarta.persistence.Entity;
import jakarta.persistence.EnumType;
import jakarta.persistence.Enumerated;
import jakarta.persistence.GeneratedValue;
import jakarta.persistence.GenerationType;
import jakarta.persistence.Id;
import jakarta.persistence.JoinColumn;
import jakarta.persistence.OneToMany;
import jakarta.persistence.OneToOne;
import jakarta.persistence.Table;
import org.hibernate.annotations.Comment;
import java.util.List;

/**
 * @author lizhong.zhang@yituishui.com
 * @since 2025/7/22
 */

@Entity
@Table(name = "guide_task_flow")
@Comment("引导任务流程表")
@Getter
@Setter
public class GuideTaskFlow extends BaseModel {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    @Column(columnDefinition = "bigint comment '主键ID'")
    private Long id;


    @Column(nullable = false, columnDefinition = "tinyint(1) comment '是否完成'")
    private boolean isComplete = false;

    @OneToOne
    @JoinColumn(name = "user_id")
    @Column(columnDefinition = "bigint comment '用户ID'")
    private User user;

    @Enumerated(EnumType.STRING)
    @Column(columnDefinition = "varchar(50) comment '模板计划类型'")
    private TemplatePlanType templatePlanType;

    @OneToMany(mappedBy = "guideTaskFlow", cascade = CascadeType.ALL)
    private List<GuideTask> tasks;


}

package com.rebell.model.mysql;


import com.rebell.enums.GuideTaskEnum;
import lombok.Getter;
import lombok.Setter;

import jakarta.persistence.Entity;
import jakarta.persistence.EnumType;
import jakarta.persistence.Enumerated;
import jakarta.persistence.FetchType;
import jakarta.persistence.GeneratedValue;
import jakarta.persistence.GenerationType;
import jakarta.persistence.Id;
import jakarta.persistence.JoinColumn;
import jakarta.persistence.ManyToOne;
import jakarta.persistence.Table;
import jakarta.persistence.Column;
import org.hibernate.annotations.Comment;

/**
 * mdp新建template/storefront任务总表
 * @author lizhong.zhang@yituishui.com
 * @since 2025/7/22
 */

@Entity
@Table(name = "guide_task")
@Comment("引导任务表")
@Getter
@Setter
public class GuideTask extends BaseModel {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    @Column(columnDefinition = "bigint comment '主键ID'")
    private Long id;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "guide_task_flow_id")
    @Column(columnDefinition = "bigint comment '引导任务流程ID'")
    private GuideTaskFlow guideTaskFlow;

    @Enumerated(EnumType.STRING)
    @Column(columnDefinition = "varchar(50) comment '任务类型'")
    private GuideTaskEnum task;

    @Column(columnDefinition = "int comment '排序号'")
    private Integer sortNo;

    @Column(columnDefinition = "tinyint(1) comment '是否完成'")
    private boolean isComplete = false;

    @Column(columnDefinition = "varchar(50) comment '用户UUID'")
    private String userUuid;
}
